const jsonString = $json["jsonString"];
const parsedData = JSON.parse(jsonString);
return parsedData;

let results = [];

for (item of items) {
    for (key of Object.keys(item.binary)) {
        results.push({
            json: {
                fileName: item.binary[key].fileName,
            sender: item.json.from.value[0].address
            },
            binary: {
                data: item.binary[key],
            }
        });
    }
}

return results;

let today = new Date();
const createdDate = today.toISOString().split('T')[0] + '_';
const filename = createdDate.toString() + $input.item.json.sender.split("@").shift();

return { json: {'filename' : filename},
       binary: {
                data: $input.item.binary.data,
            }};

let today = new Date();
