drop table public.zl_n8n_debounce;
create table public.zl_n8n_debounce (
  id bigint generated by default as identity not null,
  key text not null,
  incr bigint null default '0'::bigint,
  constraint zl_n8n_debounce_pkey primary key (id),
  constraint zl_n8n_debounce_key_key unique (key)
) TABLESPACE pg_default;
